mapper.delete:
DELETE FROM sales_record
WHERE user_id = #{userId}
      AND time = #{time}

mapper.select:
SELECT user_id, time, type, reason, invoice_id
FROM sales_record
WHERE user_id = #{userId}
      AND time = #{time}

SELECT user_id, time, type, reason, invoice_id
FROM sales_record


        AND `user_id` = #{userId}


        AND `time` >= #{minTime}


        AND `time` <= #{maxTime}


        AND `type` = #{type}


        AND `invoice_id` = #{invoiceId}
